import { call, put, takeLatest } from "redux-saga/effects";
import { actionTypes } from '../constants';
// import { api } from '../../api';

// const Api = api.instance();

function* fetchAllTodos({ callback }) {
    try {
        const URL = ""
        // const todos = yield call(() => Api.get(URL));
        // yield put(actionTypes.FETCH_TODOS_FAILURE, todos);
    } catch (err) {
        yield put(actionTypes.FETCH_TODOS_FAILURE, err)
    } finally {
        callback && typeof callback === 'function', callback()
    }
}

function* fetchTodoById({ payload, callback }) {
    try {
        const URL = `sdgsdfsdf/${payload.id}`
        // const todo = yield call(() => Api.get(URL));
        // yield put(actionTypes.FETCH_TODO_FAILURE, todo);
    } catch (err) {
        yield put(actionTypes.FETCH_TODO_FAILURE, err)
    } finally {
        callback && typeof callback === 'function', callback()
    }
}

export default function* todosWatcher() {
    yield takeLatest (actionTypes.FETCH_TODOS_REQUEST, fetchAllTodos)
    yield takeLatest (actionTypes.FETCH_TODO_REQUEST, fetchTodoById)
}
