import React, { useEffect } from "react";
import "./App.css";
import InputFieldContainer from "./components/InputField/InputFieldContainer";
import TodoList from "./components/TodoList/TodoList";
import { deleteAll, persistTodos } from "./redux/action/addTodo.action";
import { connect } from "react-redux";


const App = ({ deleteAll, persistTodos }) => {
  useEffect(() => {
    persistTodos();
  }, [persistTodos]);

  return (
    <div className="App">
      <h1> Todos </h1>
      <InputFieldContainer/>
      <TodoList />
      <div>
        <button onClick={deleteAll}>Delete All</button>
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  deleteAll: () => dispatch(deleteAll()),
  persistTodos: () => dispatch(persistTodos())
});
export default connect(
  null,
  mapDispatchToProps
)(App);
