import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { actions } from "../../store/actions";
import InputField from "../InputField/InputField";



const TodoContainer = ({ text, id }) => {
    const [isInput, toggleFieldStatus] = useState(false)
    // const [text,] = useState()

    const dispatch = useDispatch()


    const changeTodoItem = text => {
        dispatch(actions.UPDATE_TODO.REQUEST(text, id, () => {
        }))
    };

    const toggleField = () => toggleFieldStatus(isInput => !isInput)

    const deleteTodo = () => {
        dispatch(actions.REMOVE_TODO.REQUEST(id, () => {
        }))
    };

    return (
        <div className='Todo'>
            { isInput ?
                 <InputField/>
                :
                <div onClick={toggleField}
                // onChange={changeTodoItem}

                > {text} </div>
            }

            <div className='DeleteTodo' onClick={deleteTodo}> X </div>
        </div>
    );
};

export default TodoContainer
