import * as Yup from 'yup';

export const FORM = {
  enableReinitialize: true,
  validateOnChange: true,
  validationSchema: Yup.object().shape({
      text: Yup.string().required("This field is required")
  }),
  initialValues: {
      text: "",
  }
}
