import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { Formik, Form, Field } from 'formik';

import { actions } from "../../store/actions";
import { FORM } from '../../constants/formHelper';


const TodoContainer = ({ text, id }) => {
    const [isInput, toggleFieldStatus] = useState(false)
    // const [text,] = useState()

    const dispatch = useDispatch()


    const handleSubmit = text => {
        dispatch(actions.UPDATE_TODO.REQUEST(text, id, () => {
        }));
        toggleField();
    };

    const toggleField = () => toggleFieldStatus(isInput => !isInput)

    const deleteTodo = () => {
        dispatch(actions.REMOVE_TODO.REQUEST(id, () => {
        }))
    };
    console.log('text', text);
    return (
        <div className='Todo'>
            { isInput ?
                <Formik
                    onSubmit={handleSubmit}
                    enableReinitialize={true}
                    validateOnChange={true}
                    validationSchema={Yup.object().shape({
                        text: Yup.string().required(MSG.thisFieldIsRequired)
                    })}
                    initialValues={
                        text
                    }
                >
                    {({ values }) => {
                        console.log("values", values);
                        return <Form>
                            <Field type="text" name="text" />
                        </Form>
                    }}
                </Formik>
                :
                <div onClick={toggleField}
                // onChange={changeTodoItem}

                > {text} </div>
            }

            <div className='DeleteTodo' onClick={deleteTodo}> X </div>
        </div >
    );
};

export default TodoContainer
